CREATE TABLE IF NOT EXISTS `auth_group` (`id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(200) NOT NULL, `auth_module` text NOT NULL, `status` tinyint(1) DEFAULT '0', `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`)) ENGINE=InnoDB;
