CREATE TABLE IF NOT EXISTS `auth_users` (`pkUserId` int(10) unsigned NOT NULL AUTO_INCREMENT, `username` varchar(50) NOT NULL, `group` varchar(20) DEFAULT NULL COMMENT 'VALUE: admin | operator | client | buyer', `password` varchar(40) NOT NULL, `last_login_datetime` datetime DEFAULT NULL,`last_login_ip` varchar(50) DEFAULT NULL,`active` tinyint(1) NOT NULL DEFAULT '1',`fullname` varchar(50) DEFAULT NULL,`email` varchar(100) DEFAULT NULL,`perm_dashboard_view` tinyint(1) DEFAULT '0',`perm_merchandise_view` tinyint(1) DEFAULT NULL, PRIMARY KEY (`pkUserId`)) ENGINE=InnoDB;
