CREATE TABLE IF NOT EXISTS `module` (`id` int(11) NOT NULL AUTO_INCREMENT, `name` varchar(200) DEFAULT NULL,`slug` varchar(200) DEFAULT NULL,`icon` varchar(100) DEFAULT NULL,`parent` int(11) DEFAULT NULL,`sort` int(11) DEFAULT NULL,`hidden` tinyint(1) DEFAULT '0',`status` tinyint(1) DEFAULT '0', PRIMARY KEY (`id`)) ENGINE=InnoDB;
