CREATE TABLE `client` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `client_code` varchar(100) NOT NULL,  `mage_auth` varchar(100) NOT NULL,  `mage_wsdl` varchar(200) NOT NULL,  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  UNIQUE KEY `client_code` (`client_code`)) ENGINE=InnoDB DEFAULT CHARSET=latin1
CREATE TABLE `return` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `order_number` varchar(20) NOT NULL,  `email_address` varchar(50) NOT NULL,  `phone_number` varchar(20) NOT NULL,  `created_at` datetime NOT NULL,  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `updated_by` int(11) NOT NULL,  `client_id` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1
CREATE TABLE `return_item` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `return_id` int(11) NOT NULL, `sku` VARCHAR(30) NOT NULL, `return_reason` text NOT NULL,  `cancel_reason` text NOT NULL,  `status` int(11) NOT NULL,  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `updated_by` int(11) NOT NULL,  PRIMARY KEY (`id`),  KEY `return_id` (`return_id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1