CREATE TABLE `bank_confirmation` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `client_id` int(11) NOT NULL,  `order_number` varchar(15) NOT NULL,  `origin_bank` varchar(200) NOT NULL,  `dest_bank` varchar(200) NOT NULL,  `trasaction_method` varchar(200) NOT NULL,  `name` varchar(200) NOT NULL,  `transaction_date` datetime NOT NULL,  `amount` int(11) NOT NULL,  `status` tinyint(4) NOT NULL,  `reason` varchar(200) NOT NULL,  `receipt_url` varchar(300) NOT NULL,  `updated_by` int(11) NOT NULL,  `updated_at` datetime NOT NULL,  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;