CREATE TABLE `cod_confirmation` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `client_id` int(11) NOT NULL,  `order_number` varchar(15) NOT NULL,  `customer_name` varchar(200) NOT NULL,  `shipping_address` text NOT NULL,  `amount` int(11) NOT NULL,  `items` text NOT NULL,  `status` tinyint(1) NOT NULL,`updated_by` int(11) NOT NULL,  `updated_at` datetime NULL,  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  PRIMARY KEY (`id`),  KEY `client_id` (`client_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `cod_history` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `cod_id` int(11) NOT NULL,  `note` text NOT NULL,  `status` tinyint(1) NOT NULL,  `type` tinyint(1) NOT NULL,  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `created_by` int(11) NOT NULL,  PRIMARY KEY (`id`),  KEY `cod_id` (`cod_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
